public record TransferFundsUseCase(WalletRepository walletRepository, TransactionRepository transactionRepository,
                                   DomainEventPublisher eventPublisher) {

  /**
   * Transfers a specified monetary amount from one wallet to another. The process involves
   * withdrawing funds from the source wallet, adding the same amount to the destination wallet,
   * updating the wallets in the repository, and publishing a FundsTransferredEvent.
   *
   * @param fromWalletId  the unique identifier of the wallet from which funds will be debited.
   * @param toWalletId    the unique identifier of the wallet to which funds will be credited.
   * @param amount        the monetary amount to be transferred. This must be a positive number.
   * @param correlationId a unique identifier used to correlate the transfer operation across multiple systems.
   * @throws IllegalArgumentException if any of the wallets cannot be found, the amount is not greater than zero,
   *                                  or the source wallet has insufficient funds for the transfer.
   */
  public void transferFunds(UUID fromWalletId, UUID toWalletId, BigDecimal amount, String correlationId) {
    if (amount.compareTo(BigDecimal.ZERO) <= 0) {
      throw new IllegalArgumentException("Transfer amount must be greater than zero");
    }
    Wallet fromWallet = walletRepository.findById(fromWalletId)
        .orElseThrow(() -> new IllegalArgumentException("From Wallet not found"));
    Wallet toWallet = walletRepository.findById(toWalletId)
        .orElseThrow(() -> new IllegalArgumentException("To Wallet not found"));
    fromWallet.withdrawFunds(amount);
    toWallet.addFunds(amount);
    walletRepository.update(fromWallet);
    walletRepository.update(toWallet);
    FundsTransferredEvent event = new FundsTransferredEvent(fromWallet.getId(), toWallet.getId(), amount, correlationId);
    eventPublisher.publish(event);
  }
}

public void transferFunds(UUID fromWalletId, UUID toWalletId, BigDecimal amount, String correlationId) {
  if (amount.compareTo(BigDecimal.ZERO) <= 0) {
    throw new IllegalArgumentException("Transfer amount must be greater than zero");
  }
  Wallet fromWallet = walletRepository.findById(fromWalletId)
      .orElseThrow(() -> new IllegalArgumentException("From Wallet not found"));
  Wallet toWallet = walletRepository.findById(toWalletId)
      .orElseThrow(() -> new IllegalArgumentException("To Wallet not found"));
  fromWallet.withdrawFunds(amount);
  toWallet.addFunds(amount);
  walletRepository.update(fromWallet);
  walletRepository.update(toWallet);
  FundsTransferredEvent event = new FundsTransferredEvent(fromWallet.getId(), toWallet.getId(), amount, correlationId);
  eventPublisher.publish(event);
}

public record UpdateAddressStatusUseCase(
    AddressRepository addressRepository,
    DomainEventPublisher eventPublisher) {

    /**
     * Activates an address, making it available for transactions.
     *
     * @param addressId the unique identifier of the address
     * @param correlationId a unique identifier used to trace this operation
     * @return the updated address instance
     * @throws IllegalArgumentException if address not found
     */
    public Address activateAddress(UUID addressId, String correlationId) {
        if (addressId == null) {
            throw new IllegalArgumentException("Address ID must be provided");
        }

        Address address = addressRepository.findById(addressId)
                .orElseThrow(() -> new IllegalArgumentException("Address not found with id: " + addressId));

        address.activate();
        addressRepository.update(address);

        // Publish events
        address.getDomainEvents().forEach(eventPublisher::publish);
        address.clearEvents();

        return address;
    }

    /**
     * Archives an address, removing it from active use but keeping it for history.
     *
     * @param addressId the unique identifier of the address
     * @param correlationId a unique identifier used to trace this operation
     * @return the updated address instance
     * @throws IllegalArgumentException if address not found
     */
    public Address archiveAddress(UUID addressId, String correlationId) {
        if (addressId == null) {
            throw new IllegalArgumentException("Address ID must be provided");
        }

        Address address = addressRepository.findById(addressId)
                .orElseThrow(() -> new IllegalArgumentException("Address not found with id: " + addressId));

        address.archive();
        addressRepository.update(address);

        // Publish events
        address.getDomainEvents().forEach(eventPublisher::publish);
        address.clearEvents();

        return address;
    }

    /**
     * Updates the status of an address to a specific value.
     *
     * @param addressId the unique identifier of the address
     * @param newStatus the new status to set
     * @param correlationId a unique identifier used to trace this operation
     * @return the updated address instance
     * @throws IllegalArgumentException if address not found or status is null
     */
    public Address updateStatus(UUID addressId, AddressStatus newStatus, String correlationId) {
        if (addressId == null) {
            throw new IllegalArgumentException("Address ID must be provided");
        }
        if (newStatus == null) {
            throw new IllegalArgumentException("New status must be provided");
        }

        Address address = addressRepository.findById(addressId)
                .orElseThrow(() -> new IllegalArgumentException("Address not found with id: " + addressId));

        // Apply the appropriate status change
        switch (newStatus) {
            case ACTIVE -> address.activate();
            case ARCHIVED -> address.archive();
            default -> throw new IllegalArgumentException("Unsupported status transition: " + newStatus);
        }

        addressRepository.update(address);

        // Publish events
        address.getDomainEvents().forEach(eventPublisher::publish);
        address.clearEvents();

        return address;
    }

    /**
     * Batch updates the status of multiple addresses.
     *
     * @param addressIds list of address identifiers to update
     * @param newStatus the new status to set for all addresses
     * @param correlationId a unique identifier used to trace this operation
     * @return count of successfully updated addresses
     */
    public int batchUpdateStatus(UUID[] addressIds, AddressStatus newStatus, String correlationId) {
        if (addressIds == null || addressIds.length == 0) {
            return 0;
        }
        if (newStatus == null) {
            throw new IllegalArgumentException("New status must be provided");
        }

        int updatedCount = 0;
        for (UUID addressId : addressIds) {
            try {
                updateStatus(addressId, newStatus, correlationId);
                updatedCount++;
            } catch (IllegalArgumentException e) {
                // Log error but continue with other addresses
                // In production, this would use proper logging
                System.err.println("Failed to update address " + addressId + ": " + e.getMessage());
            }
        }

        return updatedCount;
    }
}

