public class AddressCreatedEvent extends DomainEvent {
    private final UUID addressId;
    private final UUID walletId;
    private final UUID networkId;
    private final String accountAddress;

    public AddressCreatedEvent(UUID addressId, UUID walletId, UUID networkId, String accountAddress, UUID correlationId) {
        super(correlationId);
        this.addressId = addressId;
        this.walletId = walletId;
        this.networkId = networkId;
        this.accountAddress = accountAddress;
    }

    public UUID getAddressId() {
        return addressId;
    }

    public UUID getWalletId() {
        return walletId;
    }

    public UUID getNetworkId() {
        return networkId;
    }

    public String getAccountAddress() {
        return accountAddress;
    }
}

public class AddressStatusChangedEvent extends DomainEvent {
    private final UUID addressId;
    private final AddressStatus oldStatus;
    private final AddressStatus newStatus;

    public AddressStatusChangedEvent(UUID addressId, AddressStatus oldStatus, AddressStatus newStatus, UUID correlationId) {
        super(correlationId);
        this.addressId = addressId;
        this.oldStatus = oldStatus;
        this.newStatus = newStatus;
    }

    public UUID getAddressId() {
        return addressId;
    }

    public AddressStatus getOldStatus() {
        return oldStatus;
    }

    public AddressStatus getNewStatus() {
        return newStatus;
    }
}

@Getter
public abstract class DomainEvent {
    private final UUID eventId;
    private final Instant occurredOn;
    private final UUID correlationId;

    protected DomainEvent(UUID correlationId) {
      this.correlationId = correlationId;
      this.eventId = UUID.randomUUID();
      this.occurredOn = Instant.now();
    }
}

public class DomainEventPublisher {
    private static final ThreadLocal<List<Consumer<DomainEvent>>> SUBSCRIBERS = new ThreadLocal<>();
    private static final ThreadLocal<Boolean> PUBLISHING = new ThreadLocal<>();

    public static void subscribe(Consumer<DomainEvent> subscriber) {
        if (PUBLISHING.get() != null && PUBLISHING.get()) {
            throw new IllegalStateException("Cannot subscribe during event publishing");
        }

        List<Consumer<DomainEvent>> subscribers = SUBSCRIBERS.get();
        if (subscribers == null) {
            subscribers = new ArrayList<>();
            SUBSCRIBERS.set(subscribers);
        }
        subscribers.add(subscriber);
    }

    public static void publish(DomainEvent event) {
        if (PUBLISHING.get() != null && PUBLISHING.get()) {
            return;
        }

        List<Consumer<DomainEvent>> subscribers = SUBSCRIBERS.get();
        if (subscribers == null || subscribers.isEmpty()) {
            return;
        }

        try {
            PUBLISHING.set(true);
            subscribers.forEach(subscriber -> subscriber.accept(event));
        } finally {
            PUBLISHING.set(false);
        }
    }

    public static void reset() {
        SUBSCRIBERS.remove();
        PUBLISHING.remove();
    }
}

public static void subscribe(Consumer<DomainEvent> subscriber) {
    if (PUBLISHING.get() != null && PUBLISHING.get()) {
        throw new IllegalStateException("Cannot subscribe during event publishing");
    }

    List<Consumer<DomainEvent>> subscribers = SUBSCRIBERS.get();
    if (subscribers == null) {
        subscribers = new ArrayList<>();
        SUBSCRIBERS.set(subscribers);
    }
    subscribers.add(subscriber);
}

public static void publish(DomainEvent event) {
    if (PUBLISHING.get() != null && PUBLISHING.get()) {
        return;
    }

    List<Consumer<DomainEvent>> subscribers = SUBSCRIBERS.get();
    if (subscribers == null || subscribers.isEmpty()) {
        return;
    }

    try {
        PUBLISHING.set(true);
        subscribers.forEach(subscriber -> subscriber.accept(event));
    } finally {
        PUBLISHING.set(false);
    }
}

public static void reset() {
    SUBSCRIBERS.remove();
    PUBLISHING.remove();
}

