@Slf4j
@Component
public class SlowQueryDetector {

    private final long thresholdMs;

    /**
     * Constructs a SlowQueryDetector with configurable threshold.
     *
     * @param thresholdMs threshold in milliseconds (default: 50ms)
     */
    public SlowQueryDetector(
            @Value("${tracing.sampling.slow-query-threshold-ms:50}") long thresholdMs) {
        this.thresholdMs = thresholdMs;
        log.info("SlowQueryDetector initialized with threshold: {}ms", thresholdMs);
    }

    /**
     * Detects if a query is slow and adds diagnostic tags to the observation.
     *
     * @param observation the observation to tag
     * @param durationMs query duration in milliseconds
     * @return true if query is slow, false otherwise
     */
    public boolean detectAndTag(Observation observation, long durationMs) {
        if (observation == null) {
            return false;
        }

        boolean isSlow = durationMs > thresholdMs;

        if (isSlow) {
            observation.lowCardinalityKeyValue("slow_query", "true");
            observation.highCardinalityKeyValue("query.duration_ms", String.valueOf(durationMs));

            log.debug("Slow query detected: {}ms (threshold: {}ms, observation: {})",
                     durationMs, thresholdMs, observation.getContext().getName());
        }

        return isSlow;
    }

    /**
     * Detects if a query is slow using Duration.
     *
     * @param observation the observation to tag
     * @param duration query duration
     * @return true if query is slow, false otherwise
     */
    public boolean detectAndTag(Observation observation, Duration duration) {
        if (duration == null) {
            return false;
        }
        return detectAndTag(observation, duration.toMillis());
    }

    /**
     * Detects if a query is slow using nanosecond timing.
     *
     * @param observation the observation to tag
     * @param startNanos start time in nanoseconds
     * @param endNanos end time in nanoseconds
     * @return true if query is slow, false otherwise
     */
    public boolean detectAndTagNanos(Observation observation, long startNanos, long endNanos) {
        long durationMs = (endNanos - startNanos) / 1_000_000;
        return detectAndTag(observation, durationMs);
    }

    /**
     * Gets the configured slow query threshold.
     *
     * @return threshold in milliseconds
     */
    public long getThresholdMs() {
        return thresholdMs;
    }

    /**
     * Checks if a duration exceeds the threshold without tagging.
     *
     * @param durationMs duration in milliseconds
     * @return true if duration exceeds threshold
     */
    public boolean isSlow(long durationMs) {
        return durationMs > thresholdMs;
    }

    /**
     * Checks if a Duration exceeds the threshold without tagging.
     *
     * @param duration the duration to check
     * @return true if duration exceeds threshold
     */
    public boolean isSlow(Duration duration) {
        return duration != null && duration.toMillis() > thresholdMs;
    }
}

public boolean detectAndTag(Observation observation, Duration duration) {
    if (duration == null) {
        return false;
    }
    return detectAndTag(observation, duration.toMillis());
}

public boolean detectAndTagNanos(Observation observation, long startNanos, long endNanos) {
    long durationMs = (endNanos - startNanos) / 1_000_000;
    return detectAndTag(observation, durationMs);
}

public long getThresholdMs() {
    return thresholdMs;
}

public boolean isSlow(Duration duration) {
    return duration != null && duration.toMillis() > thresholdMs;
}

@Slf4j
@Component
@RequiredArgsConstructor
@ConditionalOnClass(WebClient.class)
@ConditionalOnProperty(value = "tracing.features.externalApi", havingValue = "true", matchIfMissing = true)
public class WebClientTracingCustomizer implements WebClientCustomizer {

    private final Tracer tracer;
    private final Propagator propagator;
    private final SensitiveDataSanitizer sanitizer;
    private final TracingFeatureFlags featureFlags;

    /**
     * Customizes WebClient.Builder to add tracing filter.
     *
     * @param webClientBuilder the builder to customize
     */
    @Override
    public void customize(WebClient.Builder webClientBuilder) {
        if (!featureFlags.isExternalApi()) {
            log.debug("External API tracing disabled via feature flag");
            return;
        }

        webClientBuilder.filter(tracingExchangeFilterFunction());
        log.debug("WebClient tracing filter registered");
    }

    /**
     * Creates an ExchangeFilterFunction that wraps HTTP requests in CLIENT spans.
     *
     * @return the tracing filter function
     */
    private ExchangeFilterFunction tracingExchangeFilterFunction() {
        return (request, next) -> {
            // Skip if feature flag is disabled
            if (!featureFlags.isExternalApi()) {
                return next.exchange(request);
            }

            long startTime = System.nanoTime();

            // Create CLIENT span for outbound request
            Span span = tracer.nextSpan()
                             .name("HTTP %s".formatted(request.method().name()))
                             .start();

            try {
                // Add HTTP attributes
                span.tag("http.method", request.method().name());
                span.tag("http.url", sanitizeUrl(request.url()));
                span.tag("span.kind", "CLIENT");

                // Add request body size if available
                request.headers().getContentLength();
                if (request.headers().getContentLength() > 0) {
                    span.tag("http.request.body.size", String.valueOf(request.headers().getContentLength()));
                }

                // Inject W3C Trace Context into request headers
                ClientRequest tracedRequest = ClientRequest.from(request)
                    .headers(headers -> injectTraceContext(span, headers::add))
                    .build();

                log.debug("Started CLIENT span [method={}, url={}]", 
                         request.method(), sanitizeUrl(request.url()));

                // Execute request and handle response
                return next.exchange(tracedRequest)
                          .flatMap(response -> handleResponse(span, response, startTime))
                          .doOnError(error -> handleError(span, error, startTime))
                          .doFinally(signalType -> span.end());

            } catch (Exception e) {
                handleError(span, e, startTime);
                span.end();
                return Mono.error(e);
            }
        };
    }

    /**
     * Handles successful response by adding response attributes to span.
     *
     * @param span the CLIENT span
     * @param response the HTTP response
     * @param startTime request start time in nanoseconds
     * @return Mono of the response
     */
    private Mono<ClientResponse> handleResponse(Span span, ClientResponse response, long startTime) {
        try {
            // Add response attributes
            span.tag("http.status_code", String.valueOf(response.statusCode().value()));

            // Add response body size if available
            response.headers().contentLength().ifPresent(length ->
                span.tag("http.response.body.size", String.valueOf(length))
            );

            // Calculate and add duration
            long durationMs = (System.nanoTime() - startTime) / 1_000_000;
            span.tag("http.duration_ms", String.valueOf(durationMs));

            // Mark as successful if status < 400
            if (response.statusCode().is2xxSuccessful() || response.statusCode().is3xxRedirection()) {
                span.tag("status", "success");
            } else {
                span.tag("status", "error");
                span.tag("error.type", "HTTP_" + response.statusCode().value());
            }

            log.debug("Completed CLIENT span [status={}, duration={}ms]", 
                     response.statusCode().value(), durationMs);

        } catch (Exception e) {
            log.error("Error handling response in CLIENT span: {}", e.getMessage(), e);
        }

        return Mono.just(response);
    }

    /**
     * Handles request error by adding error attributes to span.
     *
     * @param span the CLIENT span
     * @param error the error that occurred
     * @param startTime request start time in nanoseconds
     */
    private void handleError(Span span, Throwable error, long startTime) {
        try {
            // Calculate duration even for errors
            long durationMs = (System.nanoTime() - startTime) / 1_000_000;
            span.tag("http.duration_ms", String.valueOf(durationMs));

            // Add error attributes
            span.tag("error.type", error.getClass().getSimpleName());
            span.tag("error.message", error.getMessage() != null ? error.getMessage() : "");
            span.tag("status", "error");

            log.warn("CLIENT span error [type={}, duration={}ms]: {}", 
                    error.getClass().getSimpleName(), durationMs, error.getMessage());

        } catch (Exception e) {
            log.error("Error handling error in CLIENT span: {}", e.getMessage(), e);
        }
    }

    /**
     * Injects W3C Trace Context into HTTP headers.
     *
     * @param span the current span
     * @param headerSetter callback to set headers
     */
    private void injectTraceContext(Span span, java.util.function.BiConsumer<String, String> headerSetter) {
        try {
            propagator.inject(span.context(), null, (carrier, key, value) -> {
                headerSetter.accept(key, value);
            });
        } catch (Exception e) {
            log.error("Error injecting trace context: {}", e.getMessage(), e);
        }
    }

    /**
     * Sanitizes URL to remove sensitive information.
     *
     * @param uri the URI to sanitize
     * @return sanitized URL string
     */
    private String sanitizeUrl(URI uri) {
        try {
            return sanitizer.sanitizeUrl(uri.toString());
        } catch (Exception e) {
            log.error("Error sanitizing URL: {}", e.getMessage(), e);
            return uri.getScheme() + "://" + uri.getHost();
        }
    }
}

@Override
public void customize(WebClient.Builder webClientBuilder) {
    if (!featureFlags.isExternalApi()) {
        log.debug("External API tracing disabled via feature flag");
        return;
    }

    webClientBuilder.filter(tracingExchangeFilterFunction());
    log.debug("WebClient tracing filter registered");
}

private ExchangeFilterFunction tracingExchangeFilterFunction() {
    return (request, next) -> {
        // Skip if feature flag is disabled
        if (!featureFlags.isExternalApi()) {
            return next.exchange(request);
        }

        long startTime = System.nanoTime();

        // Create CLIENT span for outbound request
        Span span = tracer.nextSpan()
                         .name("HTTP %s".formatted(request.method().name()))
                         .start();

        try {
            // Add HTTP attributes
            span.tag("http.method", request.method().name());
            span.tag("http.url", sanitizeUrl(request.url()));
            span.tag("span.kind", "CLIENT");

            // Add request body size if available
            request.headers().getContentLength();
            if (request.headers().getContentLength() > 0) {
                span.tag("http.request.body.size", String.valueOf(request.headers().getContentLength()));
            }

            // Inject W3C Trace Context into request headers
            ClientRequest tracedRequest = ClientRequest.from(request)
                .headers(headers -> injectTraceContext(span, headers::add))
                .build();

            log.debug("Started CLIENT span [method={}, url={}]", 
                     request.method(), sanitizeUrl(request.url()));

            // Execute request and handle response
            return next.exchange(tracedRequest)
                      .flatMap(response -> handleResponse(span, response, startTime))
                      .doOnError(error -> handleError(span, error, startTime))
                      .doFinally(signalType -> span.end());

        } catch (Exception e) {
            handleError(span, e, startTime);
            span.end();
            return Mono.error(e);
        }
    };
}

private Mono<ClientResponse> handleResponse(Span span, ClientResponse response, long startTime) {
    try {
        // Add response attributes
        span.tag("http.status_code", String.valueOf(response.statusCode().value()));

        // Add response body size if available
        response.headers().contentLength().ifPresent(length ->
            span.tag("http.response.body.size", String.valueOf(length))
        );

        // Calculate and add duration
        long durationMs = (System.nanoTime() - startTime) / 1_000_000;
        span.tag("http.duration_ms", String.valueOf(durationMs));

        // Mark as successful if status < 400
        if (response.statusCode().is2xxSuccessful() || response.statusCode().is3xxRedirection()) {
            span.tag("status", "success");
        } else {
            span.tag("status", "error");
            span.tag("error.type", "HTTP_" + response.statusCode().value());
        }

        log.debug("Completed CLIENT span [status={}, duration={}ms]", 
                 response.statusCode().value(), durationMs);

    } catch (Exception e) {
        log.error("Error handling response in CLIENT span: {}", e.getMessage(), e);
    }

    return Mono.just(response);
}

private void handleError(Span span, Throwable error, long startTime) {
    try {
        // Calculate duration even for errors
        long durationMs = (System.nanoTime() - startTime) / 1_000_000;
        span.tag("http.duration_ms", String.valueOf(durationMs));

        // Add error attributes
        span.tag("error.type", error.getClass().getSimpleName());
        span.tag("error.message", error.getMessage() != null ? error.getMessage() : "");
        span.tag("status", "error");

        log.warn("CLIENT span error [type={}, duration={}ms]: {}", 
                error.getClass().getSimpleName(), durationMs, error.getMessage());

    } catch (Exception e) {
        log.error("Error handling error in CLIENT span: {}", e.getMessage(), e);
    }
}

private void injectTraceContext(Span span, java.util.function.BiConsumer<String, String> headerSetter) {
    try {
        propagator.inject(span.context(), null, (carrier, key, value) -> {
            headerSetter.accept(key, value);
        });
    } catch (Exception e) {
        log.error("Error injecting trace context: {}", e.getMessage(), e);
    }
}

private String sanitizeUrl(URI uri) {
    try {
        return sanitizer.sanitizeUrl(uri.toString());
    } catch (Exception e) {
        log.error("Error sanitizing URL: {}", e.getMessage(), e);
        return uri.getScheme() + "://" + uri.getHost();
    }
}

@Slf4j
@Component
@Order(Ordered.HIGHEST_PRECEDENCE + 100)
@RequiredArgsConstructor
@ConditionalOnProperty(value = "management.tracing.enabled", havingValue = "true", matchIfMissing = true)
public class WebFluxTracingFilter implements WebFilter {

    private final ObservationRegistry observationRegistry;
    private final Tracer tracer;

    /**
     * Filters incoming HTTP requests to establish trace context.
     *
     * @param exchange the server web exchange
     * @param chain the filter chain
     * @return Mono that completes when the request processing is done
     */
    @Override
    public Mono<Void> filter(ServerWebExchange exchange, WebFilterChain chain) {
        String method = exchange.getRequest().getMethod().name();
        String path = exchange.getRequest().getPath().value();
        String observationName = "http.server.%s %s".formatted(method, path);

        // Create observation for this HTTP request
        Observation observation = Observation.createNotStarted(observationName, observationRegistry)
                .contextualName(method + " " + path)
                .lowCardinalityKeyValue("http.method", method)
                .lowCardinalityKeyValue("http.route", path);

        return observation.observe(() -> {
            // Add request attributes
            addRequestAttributes(observation, exchange);

            // Process request and capture response status
            return chain.filter(exchange)
                    .doOnSuccess(v -> {
                        int statusCode = exchange.getResponse().getStatusCode() != null
                                ? exchange.getResponse().getStatusCode().value()
                                : 200;
                        observation.lowCardinalityKeyValue("http.status_code", String.valueOf(statusCode));
                        observation.lowCardinalityKeyValue("status", "success");

                        log.trace("HTTP request traced: {} {} - {}", method, path, statusCode);
                    })
                    .doOnError(error -> {
                        int statusCode = exchange.getResponse().getStatusCode() != null
                                ? exchange.getResponse().getStatusCode().value()
                                : 500;
                        observation.lowCardinalityKeyValue("http.status_code", String.valueOf(statusCode));
                        observation.lowCardinalityKeyValue("status", "error");
                        observation.error(error);

                        log.debug("HTTP request traced with error: {} {} - {}", 
                                 method, path, error.getClass().getSimpleName());
                    })
                    .contextWrite(ctx -> addObservationToContext(ctx, observation));
        });
    }

    /**
     * Adds HTTP request attributes to the observation.
     *
     * @param observation the observation
     * @param exchange the server web exchange
     */
    private void addRequestAttributes(Observation observation, ServerWebExchange exchange) {
        try {
            // Extract traceparent header if present (W3C Trace Context 1.0)
            String traceparent = exchange.getRequest().getHeaders().getFirst("traceparent");
            if (traceparent != null) {
                observation.lowCardinalityKeyValue("trace.parent", "present");
                log.trace("Extracted W3C Trace Context traceparent header: {}", 
                         maskTraceId(traceparent));
            } else {
                observation.lowCardinalityKeyValue("trace.parent", "absent");
                log.trace("No traceparent header present, creating root span");
            }

            // Extract tracestate header if present
            String tracestate = exchange.getRequest().getHeaders().getFirst("tracestate");
            if (tracestate != null) {
                observation.lowCardinalityKeyValue("trace.state", "present");
                log.trace("Extracted tracestate header");
            }

            // Add URL (sanitized - query params will be masked by sanitizer if needed)
            String url = exchange.getRequest().getURI().toString();
            observation.highCardinalityKeyValue("http.url", sanitizeUrl(url));

            // Add user agent if present
            String userAgent = exchange.getRequest().getHeaders().getFirst("User-Agent");
            if (userAgent != null) {
                observation.highCardinalityKeyValue("http.user_agent", truncate(userAgent, 256));
            }

            // Add client IP (X-Forwarded-For or remote address)
            String clientIp = extractClientIp(exchange);
            if (clientIp != null) {
                observation.highCardinalityKeyValue("http.client_ip", maskIp(clientIp));
            }

        } catch (Exception e) {
            log.warn("Failed to extract request attributes: {}", e.getMessage());
        }
    }

    /**
     * Adds the observation to the Reactor context for propagation.
     *
     * @param ctx the current context
     * @param observation the observation to add
     * @return updated context
     */
    private Context addObservationToContext(Context ctx, Observation observation) {
        // Note: Micrometer automatically handles observation propagation via
        // reactor-context-propagation. This method is here for explicit documentation
        // and potential custom context keys.
        return ctx;
    }

    /**
     * Extracts client IP address from request headers or remote address.
     *
     * @param exchange the server web exchange
     * @return client IP address or null
     */
    private String extractClientIp(ServerWebExchange exchange) {
        // Check X-Forwarded-For header (proxy/load balancer scenarios)
        String xForwardedFor = exchange.getRequest().getHeaders().getFirst("X-Forwarded-For");
        if (xForwardedFor != null && !xForwardedFor.isEmpty()) {
            // Take first IP in the chain
            return xForwardedFor.split(",")[0].trim();
        }

        // Check X-Real-IP header
        String xRealIp = exchange.getRequest().getHeaders().getFirst("X-Real-IP");
        if (xRealIp != null && !xRealIp.isEmpty()) {
            return xRealIp;
        }

        // Fall back to remote address
        if (exchange.getRequest().getRemoteAddress() != null) {
            return exchange.getRequest().getRemoteAddress().getAddress().getHostAddress();
        }

        return null;
    }

    /**
     * Sanitizes URL by masking query parameters that may contain sensitive data.
     *
     * @param url the URL to sanitize
     * @return sanitized URL
     */
    private String sanitizeUrl(String url) {
        if (url == null) {
            return "";
        }

        // Mask common sensitive query parameters
        String sanitized = url.replaceAll("([?&])(token|password|secret|key|auth)=([^&]*)", "$1$2=***");

        return truncate(sanitized, 512);
    }

    /**
     * Masks IP address for privacy (keeps first two octets).
     *
     * @param ip the IP address
     * @return masked IP
     */
    private String maskIp(String ip) {
        if (ip == null) {
            return "";
        }

        // IPv4: keep first two octets
        if (ip.contains(".")) {
            String[] parts = ip.split("\\.");
            if (parts.length == 4) {
                return parts[0] + "." + parts[1] + ".***.**";
            }
        }

        // IPv6: keep first two groups
        if (ip.contains(":")) {
            String[] parts = ip.split(":");
            if (parts.length >= 2) {
                return parts[0] + ":" + parts[1] + ":****";
            }
        }

        return "masked";
    }

    /**
     * Masks trace ID for logging (shows only first 8 characters).
     *
     * @param traceparent the traceparent header value
     * @return masked trace ID
     */
    private String maskTraceId(String traceparent) {
        if (traceparent == null || traceparent.length() < 16) {
            return "invalid";
        }

        try {
            // traceparent format: 00-{trace-id}-{parent-id}-{flags}
            String[] parts = traceparent.split("-");
            if (parts.length >= 2) {
                String traceId = parts[1];
                return traceId.substring(0, Math.min(8, traceId.length())) + "...";
            }
        } catch (Exception e) {
            // Ignore parsing errors
        }

        return "masked";
    }

    /**
     * Truncates a string to the specified length.
     *
     * @param value the value to truncate
     * @param maxLength maximum length
     * @return truncated value
     */
    private String truncate(String value, int maxLength) {
        if (value == null) {
            return "";
        }
        if (value.length() <= maxLength) {
            return value;
        }
        return value.substring(0, maxLength - 3) + "...";
    }
}

@Override
public Mono<Void> filter(ServerWebExchange exchange, WebFilterChain chain) {
    String method = exchange.getRequest().getMethod().name();
    String path = exchange.getRequest().getPath().value();
    String observationName = "http.server.%s %s".formatted(method, path);

    // Create observation for this HTTP request
    Observation observation = Observation.createNotStarted(observationName, observationRegistry)
            .contextualName(method + " " + path)
            .lowCardinalityKeyValue("http.method", method)
            .lowCardinalityKeyValue("http.route", path);

    return observation.observe(() -> {
        // Add request attributes
        addRequestAttributes(observation, exchange);

        // Process request and capture response status
        return chain.filter(exchange)
                .doOnSuccess(v -> {
                    int statusCode = exchange.getResponse().getStatusCode() != null
