@Slf4j
@Component
@RequiredArgsConstructor
@ConditionalOnClass(Connection.class)
@ConditionalOnProperty(value = "tracing.features.database", havingValue = "true", matchIfMissing = true)
public class R2dbcObservationHandler implements ObservationHandler<Observation.Context> {

    private final SpanAttributeBuilder spanAttributeBuilder;
    private final TracingFeatureFlags featureFlags;
    private final ConnectionPool connectionPool; // Injected if available

    /**
     * Determines if this handler supports the given observation context.
     * Only handles R2DBC-related observations.
     *
     * @param context the observation context
     * @return true if this is an R2DBC observation
     */
    @Override
    public boolean supportsContext(Observation.Context context) {
        // Check if this is an R2DBC observation by context name or type
        if (context == null || !featureFlags.isDatabase()) {
            return false;
        }

        String name = context.getName();
        return name != null && (name.startsWith("r2dbc.") || name.contains("connection"));
    }

    /**
     * Called when the observation starts (connection acquisition begins).
     * Records start time for connection acquisition timing.
     *
     * @param context the observation context
     */
    @Override
    public void onStart(Observation.Context context) {
        if (!featureFlags.isDatabase()) {
            return;
        }

        try {
            // Record start time for connection acquisition
            context.put("connection.acquisition.start", System.nanoTime());

            log.trace("R2DBC observation started: {}", context.getName());
        } catch (Exception e) {
            log.warn("Failed to start R2DBC observation: {}", e.getMessage());
        }
    }

    /**
     * Called when the observation stops (query execution completes).
     * Adds span attributes including timing, pool metrics, and operation details.
     *
     * @param context the observation context
     */
    @Override
    public void onStop(Observation.Context context) {
        if (!featureFlags.isDatabase()) {
            return;
        }

        try {
            // Calculate connection acquisition time
            Long acquisitionStart = context.get("connection.acquisition.start");
            if (acquisitionStart != null) {
                long acquisitionTimeNs = System.nanoTime() - acquisitionStart;
                double acquisitionTimeMs = acquisitionTimeNs / 1_000_000.0;

                context.addLowCardinalityKeyValue(
                        KeyValue.of("db.connection.acquisition_time_ms",
                                "%.2f".formatted(acquisitionTimeMs))
                );
            }

            // Add database system
            addDatabaseAttributes(context);

            // Add connection pool metrics
            addConnectionPoolMetrics(context);

            // Mark as successful
            context.addLowCardinalityKeyValue(KeyValue.of("status", "success"));

            log.trace("R2DBC observation completed: {}", context.getName());
        } catch (Exception e) {
            log.warn("Failed to stop R2DBC observation: {}", e.getMessage());
        }
    }

    /**
     * Called when the observation encounters an error.
     * Adds error attributes to the span.
     *
     * @param context the observation context
     */
    @Override
    public void onError(Observation.Context context) {
        if (!featureFlags.isDatabase()) {
            return;
        }

        try {
            Throwable error = context.getError();
            if (error != null) {
                context.addLowCardinalityKeyValue(KeyValue.of(SpanAttributeBuilder.ERROR, "true"));
                context.addLowCardinalityKeyValue(KeyValue.of(SpanAttributeBuilder.ERROR_TYPE, 
                                                  error.getClass().getSimpleName()));

                String message = error.getMessage();
                if (message != null) {
                    context.addHighCardinalityKeyValue(KeyValue.of(SpanAttributeBuilder.ERROR_MESSAGE, 
                                                       truncate(message, 512)));
                }

                context.addLowCardinalityKeyValue(KeyValue.of("status", "error"));
            }

            log.debug("R2DBC observation error: {} - {}", 
                     context.getName(), 
                     error != null ? error.getClass().getSimpleName() : "unknown");
        } catch (Exception e) {
            log.warn("Failed to handle R2DBC observation error: {}", e.getMessage());
        }
    }

    /**
     * Adds database system and operation attributes.
     *
     * @param context the observation context
     */
    private void addDatabaseAttributes(Observation.Context context) {
        // Determine database system from context or connection pool
        String dbSystem = determineDatabaseSystem();
        context.addLowCardinalityKeyValue(KeyValue.of(SpanAttributeBuilder.DB_SYSTEM, dbSystem));

        // Try to derive operation from context name
        String operation = deriveOperation(context.getName());
        if (operation != null) {
            context.addLowCardinalityKeyValue(KeyValue.of(SpanAttributeBuilder.DB_OPERATION, operation));
        }
    }

    /**
     * Adds connection pool metrics to the span.
     *
     * @param context the observation context
     */
    private void addConnectionPoolMetrics(Observation.Context context) {
        if (connectionPool == null) {
            return;
        }

        try {
            PoolMetrics metrics = connectionPool.getMetrics().orElse(null);
            if (metrics != null) {
                // Active connections
                context.addLowCardinalityKeyValue(
                        KeyValue.of("db.connection_pool.active",
                        String.valueOf(metrics.acquiredSize()))
                );

                // Idle connections
                context.addLowCardinalityKeyValue(
                        KeyValue.of("db.connection_pool.idle",
                        String.valueOf(metrics.idleSize()))
                );

                // Max pool size
                context.addLowCardinalityKeyValue(
                        KeyValue.of("db.connection_pool.max",
                        String.valueOf(metrics.getMaxAllocatedSize()))
                );

                // Pending acquisitions (waiting for connection)
                context.addLowCardinalityKeyValue(
                        KeyValue.of("db.connection_pool.pending",
                        String.valueOf(metrics.pendingAcquireSize()))
                );

                // Pool utilization percentage
                int maxSize = metrics.getMaxAllocatedSize();
                if (maxSize > 0) {
                    double utilization = (metrics.acquiredSize() * 100.0) / maxSize;
                    context.addLowCardinalityKeyValue(
                            KeyValue.of("db.connection_pool.utilization_percent",
                                    "%.1f".formatted(utilization))
                    );
                }
            }
        } catch (Exception e) {
            log.debug("Failed to add connection pool metrics: {}", e.getMessage());
        }
    }

    /**
     * Determines the database system from connection pool configuration.
     *
     * @return database system name
     */
    private String determineDatabaseSystem() {
        // Try to determine from connection pool metadata
        // For now, return a default value
        // In production, this would inspect the R2DBC URL or connection factory
        return "postgresql"; // TODO: Extract from ConnectionFactory
    }

    /**
     * Derives the database operation from observation name.
     *
     * @param name the observation name
     * @return operation type or null
     */
    private String deriveOperation(String name) {
        if (name == null) {
            return null;
        }

        String lowerName = name.toLowerCase();
        if (lowerName.contains("select") || lowerName.contains("find") || lowerName.contains("query")) {
            return "SELECT";
        } else if (lowerName.contains("insert") || lowerName.contains("save")) {
            return "INSERT";
        } else if (lowerName.contains("update")) {
            return "UPDATE";
        } else if (lowerName.contains("delete")) {
            return "DELETE";
        } else if (lowerName.contains("connection")) {
            return "CONNECT";
        }

        return null;
    }

    /**
     * Truncates a string to the specified length.
     *
     * @param value the value to truncate
     * @param maxLength maximum length
     * @return truncated value
     */
    private String truncate(String value, int maxLength) {
        if (value == null) {
            return "";
        }
        if (value.length() <= maxLength) {
            return value;
        }
        return value.substring(0, maxLength - 3) + "...";
    }
}

@Override
public boolean supportsContext(Observation.Context context) {
    // Check if this is an R2DBC observation by context name or type
    if (context == null || !featureFlags.isDatabase()) {
        return false;
    }

    String name = context.getName();
    return name != null && (name.startsWith("r2dbc.") || name.contains("connection"));
}

@Override
public void onStart(Observation.Context context) {
    if (!featureFlags.isDatabase()) {
        return;
    }

    try {
        // Record start time for connection acquisition
        context.put("connection.acquisition.start", System.nanoTime());

        log.trace("R2DBC observation started: {}", context.getName());
    } catch (Exception e) {
        log.warn("Failed to start R2DBC observation: {}", e.getMessage());
    }
}

@Override
public void onStop(Observation.Context context) {
    if (!featureFlags.isDatabase()) {
        return;
    }

    try {
        // Calculate connection acquisition time
        Long acquisitionStart = context.get("connection.acquisition.start");
        if (acquisitionStart != null) {
            long acquisitionTimeNs = System.nanoTime() - acquisitionStart;
            double acquisitionTimeMs = acquisitionTimeNs / 1_000_000.0;

            context.addLowCardinalityKeyValue(
                    KeyValue.of("db.connection.acquisition_time_ms",
                            "%.2f".formatted(acquisitionTimeMs))
            );
        }

        // Add database system
        addDatabaseAttributes(context);

        // Add connection pool metrics
        addConnectionPoolMetrics(context);

        // Mark as successful
        context.addLowCardinalityKeyValue(KeyValue.of("status", "success"));

        log.trace("R2DBC observation completed: {}", context.getName());
    } catch (Exception e) {
        log.warn("Failed to stop R2DBC observation: {}", e.getMessage());
    }
}

@Override
public void onError(Observation.Context context) {
    if (!featureFlags.isDatabase()) {
        return;
    }

    try {
        Throwable error = context.getError();
        if (error != null) {
            context.addLowCardinalityKeyValue(KeyValue.of(SpanAttributeBuilder.ERROR, "true"));
            context.addLowCardinalityKeyValue(KeyValue.of(SpanAttributeBuilder.ERROR_TYPE, 
                                              error.getClass().getSimpleName()));

            String message = error.getMessage();
            if (message != null) {
                context.addHighCardinalityKeyValue(KeyValue.of(SpanAttributeBuilder.ERROR_MESSAGE, 
                                                   truncate(message, 512)));
            }

            context.addLowCardinalityKeyValue(KeyValue.of("status", "error"));
        }

        log.debug("R2DBC observation error: {} - {}", 
                 context.getName(), 
                 error != null ? error.getClass().getSimpleName() : "unknown");
    } catch (Exception e) {
        log.warn("Failed to handle R2DBC observation error: {}", e.getMessage());
    }
}

private void addDatabaseAttributes(Observation.Context context) {
    // Determine database system from context or connection pool
    String dbSystem = determineDatabaseSystem();
    context.addLowCardinalityKeyValue(KeyValue.of(SpanAttributeBuilder.DB_SYSTEM, dbSystem));

    // Try to derive operation from context name
    String operation = deriveOperation(context.getName());
    if (operation != null) {
        context.addLowCardinalityKeyValue(KeyValue.of(SpanAttributeBuilder.DB_OPERATION, operation));
    }
}

private void addConnectionPoolMetrics(Observation.Context context) {
    if (connectionPool == null) {
        return;
    }

    try {
        PoolMetrics metrics = connectionPool.getMetrics().orElse(null);
        if (metrics != null) {
            // Active connections
            context.addLowCardinalityKeyValue(
                    KeyValue.of("db.connection_pool.active",
                    String.valueOf(metrics.acquiredSize()))
            );

            // Idle connections
            context.addLowCardinalityKeyValue(
                    KeyValue.of("db.connection_pool.idle",
                    String.valueOf(metrics.idleSize()))
            );

            // Max pool size
            context.addLowCardinalityKeyValue(
                    KeyValue.of("db.connection_pool.max",
                    String.valueOf(metrics.getMaxAllocatedSize()))
            );

            // Pending acquisitions (waiting for connection)
            context.addLowCardinalityKeyValue(
                    KeyValue.of("db.connection_pool.pending",
                    String.valueOf(metrics.pendingAcquireSize()))
            );

            // Pool utilization percentage
            int maxSize = metrics.getMaxAllocatedSize();
            if (maxSize > 0) {
                double utilization = (metrics.acquiredSize() * 100.0) / maxSize;
                context.addLowCardinalityKeyValue(
                        KeyValue.of("db.connection_pool.utilization_percent",
                                "%.1f".formatted(utilization))
                );
            }
        }
    } catch (Exception e) {
        log.debug("Failed to add connection pool metrics: {}", e.getMessage());
    }
}

private String determineDatabaseSystem() {
    // Try to determine from connection pool metadata
    // For now, return a default value
    // In production, this would inspect the R2DBC URL or connection factory
    return "postgresql"; // TODO: Extract from ConnectionFactory
}

private String deriveOperation(String name) {
    if (name == null) {
        return null;
    }

    String lowerName = name.toLowerCase();
    if (lowerName.contains("select") || lowerName.contains("find") || lowerName.contains("query")) {
        return "SELECT";
    } else if (lowerName.contains("insert") || lowerName.contains("save")) {
        return "INSERT";
    } else if (lowerName.contains("update")) {
        return "UPDATE";
    } else if (lowerName.contains("delete")) {
        return "DELETE";
    } else if (lowerName.contains("connection")) {
        return "CONNECT";
    }

    return null;
}

private String truncate(String value, int maxLength) {
    if (value == null) {
        return "";
    }
    if (value.length() <= maxLength) {
        return value;
    }
    return value.substring(0, maxLength - 3) + "...";
}

@Slf4j
@Component
@RequiredArgsConstructor
@ConditionalOnClass(StateMachine.class)
@ConditionalOnProperty(value = "tracing.features.stateMachine", havingValue = "true", matchIfMissing = true)
public class StateMachineObservationHandler extends StateMachineListenerAdapter<SagaStates, SagaEvents> {

    private final Tracer tracer;
    private final SpanAttributeBuilder spanAttributeBuilder;
    private final TracingFeatureFlags featureFlags;

    /**
     * Threshold for slow transition detection (milliseconds).
     * Transitions exceeding this duration are flagged with slow_transition=true.
     */
    private static final long SLOW_TRANSITION_THRESHOLD_MS = 5000; // 5 seconds

    /**
     * Tracks transition start times for duration calculation.
     * Key: state machine ID, Value: transition start timestamp (nanos)
     */
    private final Map<String, Long> transitionStartTimes = new ConcurrentHashMap<>();

    /**
     * Tracks active spans for each state machine instance.
     * Key: state machine ID, Value: current transition span
     */
    private final Map<String, Span> activeSpans = new ConcurrentHashMap<>();

    /**
     * Called when a state machine transition starts.
     * Creates a new span for the transition with initial attributes.
     *
     * @param transition the transition being executed
     * @param stateMachine the state machine instance
     */
    public void transitionStarted(Transition<SagaStates, SagaEvents> transition, StateMachine<SagaStates, SagaEvents> stateMachine) {
        if (!featureFlags.isStateMachine()) {
            return;
        }

        try {
            String machineId = getMachineId(stateMachine);
            State<SagaStates, SagaEvents> sourceState = transition.getSource();
            State<SagaStates, SagaEvents> targetState = transition.getTarget();
            SagaEvents event = transition.getTrigger() != null ? transition.getTrigger().getEvent() : null;

            // Create new span for transition
            Span span = tracer.nextSpan().name("State Transition: %s  %s".formatted(
                    getStateName(sourceState),
                    getStateName(targetState)));
            span.start();

            // Add state machine attributes
            span.tag("statemachine.id", machineId);
            span.tag("statemachine.type", "saga");
            span.tag("statemachine.state.from", getStateName(sourceState));
            span.tag("statemachine.state.to", getStateName(targetState));

            if (event != null) {
                span.tag("statemachine.event", event.name());
            }

            // Detect compensation flows (transitions to FAILED state)
            if (targetState != null && targetState.getId() == SagaStates.FAILED) {
                span.tag("statemachine.compensation", "true");
                log.debug("Compensation flow detected: {}  FAILED", getStateName(sourceState));
            }

            // Record transition start time
            long startTime = System.nanoTime();
            transitionStartTimes.put(machineId, startTime);
            activeSpans.put(machineId, span);

            // Add transition started event
            span.event("transition.started");

            log.debug("Started transition span [machineId={}, from={}, to={}, event={}]",
                     machineId, getStateName(sourceState), getStateName(targetState), event);

        } catch (Exception e) {
            log.error("Error in transitionStarted: {}", e.getMessage(), e);
        }
    }

    /**
     * Called when a state machine transition ends.
     * Completes the transition span with final attributes and duration.
     *
     * @param transition the completed transition
     * @param stateMachine the state machine instance
     */
    public void transitionEnded(Transition<SagaStates, SagaEvents> transition, StateMachine<SagaStates, SagaEvents> stateMachine) {
        if (!featureFlags.isStateMachine()) {
            return;
        }

        try {
            String machineId = getMachineId(stateMachine);
            Span span = activeSpans.remove(machineId);
            Long startTime = transitionStartTimes.remove(machineId);

            if (span != null) {
                // Calculate transition duration
                if (startTime != null) {
                    long durationMs = (System.nanoTime() - startTime) / 1_000_000;
                    span.tag("statemachine.transition.duration_ms", String.valueOf(durationMs));

                    // Flag slow transitions
                    if (durationMs > SLOW_TRANSITION_THRESHOLD_MS) {
                        span.tag("statemachine.slow_transition", "true");
                        log.warn("Slow state machine transition detected [machineId={}, duration={}ms, threshold={}ms]",
                                machineId, durationMs, SLOW_TRANSITION_THRESHOLD_MS);
                    }
                }

                // Add completion event and end span
                span.event("transition.completed");
                span.end();

                State<SagaStates, SagaEvents> targetState = transition.getTarget();
                log.debug("Completed transition span [machineId={}, targetState={}]",
                         machineId, getStateName(targetState));
            }

        } catch (Exception e) {
            log.error("Error in transitionEnded: {}", e.getMessage(), e);
        }
    }

    /**
     * Called when a state change occurs.
     * Adds span events for the state change.
     *
     * @param from the previous state
     * @param to the new state
     * @param stateMachine the state machine instance
     */
    public void stateChanged(State<SagaStates, SagaEvents> from, State<SagaStates, SagaEvents> to, 
                            StateMachine<SagaStates, SagaEvents> stateMachine) {
        if (!featureFlags.isStateMachine()) {
            return;
        }

        try {
            String machineId = getMachineId(stateMachine);
            Span span = activeSpans.get(machineId);

            if (span != null) {
                span.event("state.changed: %s  %s".formatted(getStateName(from), getStateName(to)));
                log.debug("State changed [machineId={}, from={}, to={}]", machineId, getStateName(from), getStateName(to));
            }

        } catch (Exception e) {
            log.error("Error in stateChanged: {}", e.getMessage(), e);
        }
    }

    /**
     * Called when a state context message is received during extended state change.
     * This can be used to track guard evaluations and action executions.
     *
     * @param message the state context message
     * @param stateMachine the state machine instance
     */
    public void extendedStateChanged(Object key, Object value, StateMachine<SagaStates, SagaEvents> stateMachine) {
        if (!featureFlags.isStateMachine()) {
            return;
        }

        try {
            String machineId = getMachineId(stateMachine);
            Span span = activeSpans.get(machineId);

            if (span != null && key != null) {
                // Track guard evaluations
                if (key.toString().startsWith("guard.")) {
                    span.event("guard.evaluated: %s = %s".formatted(key, value));
                    span.tag(key.toString(), String.valueOf(value));
                    log.debug("Guard evaluated [machineId={}, guard={}, result={}]", machineId, key, value);
                }

                // Track action executions
                if (key.toString().startsWith("action.")) {
                    span.event("action.executed: %s".formatted(key));
                    log.debug("Action executed [machineId={}, action={}]", machineId, key);
                }
            }

        } catch (Exception e) {
            log.error("Error in extendedStateChanged: {}", e.getMessage(), e);
        }
    }

    /**
     * Called when a state machine encounters an error.
     * Marks the transition span as error and adds exception details.
     *
     * @param stateMachine the state machine instance
     * @param exception the exception that occurred
     */
    public void stateMachineError(StateMachine<SagaStates, SagaEvents> stateMachine, Exception exception) {
        if (!featureFlags.isStateMachine()) {
            return;
        }

        try {
            String machineId = getMachineId(stateMachine);
            Span span = activeSpans.get(machineId);

            if (span != null) {
                span.tag("error.type", exception.getClass().getSimpleName());
                span.tag("error.message", exception.getMessage() != null ? exception.getMessage() : "");
                span.tag("status", "error");
                span.event("statemachine.error");

                log.error("State machine error [machineId={}]: {}", machineId, exception.getMessage(), exception);
            }

        } catch (Exception e) {
            log.error("Error in stateMachineError: {}", e.getMessage(), e);
        }
    }

    /**
     * Extracts the state machine ID from the machine instance.
     * Uses the machine's UUID if available, otherwise generates one.
     *
     * @param stateMachine the state machine instance
     * @return the machine ID
     */
    private String getMachineId(StateMachine<SagaStates, SagaEvents> stateMachine) {
        return Optional.ofNullable(stateMachine.getId())
                       .orElse(stateMachine.getUuid().toString());
    }

    /**
     * Extracts the state name from a State object.
     *
     * @param state the state object
     * @return the state name, or "UNKNOWN" if null
     */
    private String getStateName(State<SagaStates, SagaEvents> state) {
        return state != null && state.getId() != null 
               ? state.getId().name() 
               : "UNKNOWN";
    }
}

public void transitionStarted(Transition<SagaStates, SagaEvents> transition, StateMachine<SagaStates, SagaEvents> stateMachine) {
    if (!featureFlags.isStateMachine()) {
        return;
    }

    try {
        String machineId = getMachineId(stateMachine);
        State<SagaStates, SagaEvents> sourceState = transition.getSource();
        State<SagaStates, SagaEvents> targetState = transition.getTarget();
        SagaEvents event = transition.getTrigger() != null ? transition.getTrigger().getEvent() : null;

        // Create new span for transition
        Span span = tracer.nextSpan().name("State Transition: %s  %s".formatted(
                getStateName(sourceState),
                getStateName(targetState)));
        span.start();

        // Add state machine attributes
        span.tag("statemachine.id", machineId);
        span.tag("statemachine.type", "saga");
        span.tag("statemachine.state.from", getStateName(sourceState));
        span.tag("statemachine.state.to", getStateName(targetState));

        if (event != null) {
            span.tag("statemachine.event", event.name());
        }

        // Detect compensation flows (transitions to FAILED state)
        if (targetState != null && targetState.getId() == SagaStates.FAILED) {
            span.tag("statemachine.compensation", "true");
            log.debug("Compensation flow detected: {}  FAILED", getStateName(sourceState));
        }

        // Record transition start time
        long startTime = System.nanoTime();
        transitionStartTimes.put(machineId, startTime);
        activeSpans.put(machineId, span);

        // Add transition started event
        span.event("transition.started");

        log.debug("Started transition span [machineId={}, from={}, to={}, event={}]",
                 machineId, getStateName(sourceState), getStateName(targetState), event);

    } catch (Exception e) {
        log.error("Error in transitionStarted: {}", e.getMessage(), e);
    }
