            // Add completion event and end span
            span.event("transition.completed");
            span.end();

            State<SagaStates, SagaEvents> targetState = transition.getTarget();
            log.debug("Completed transition span [machineId={}, targetState={}]",
                     machineId, getStateName(targetState));
        }

    } catch (Exception e) {
        log.error("Error in transitionEnded: {}", e.getMessage(), e);
    }
}

